								var next = node.nextSibling;
								if(!next)	break;
								if(next.nodeType != 3)	break;

								var m = DomNodeGetNodeValue(next).match(new RegExp("^([a-zA-Z0-9+/.]{8,10})[●!]{0,2}","i"));
								if(!m)	break;

								// 元のテキストノード
								DomNodeSetNodeValue(next,m[0]);

