		// --------------------------------------------------------------------------------
		// プリセットを除去
		// --------------------------------------------------------------------------------
		_container.removePresetFromObject = function(obj){
			var p;
			for(p in obj){
				var a = obj[p];
				if(typeof(a) == "object"){
					if(a.constructor == Array){
						var i;
						var num = a.length;
						for(i=0;i<num;i++){
							var o = a[i];
							if(o.id !== undefined){
								if(o.preset !== undefined){
									ObjectDeleteProperty(o,"preset");
								}
							}
						}
					}
				}
			}
			return obj;
		}

		// --------------------------------------------------------------------------------
		// プリセットをマージ
		// --------------------------------------------------------------------------------
		_container.margePresetFromObject = function(obj){
			if(obj.version === undefined)	return null;

			var p;
			var preset = ProjectUpdate(null,{version:obj.version});

			// 辞書を作成
			var dictionary = new Object();
			for(p in preset){
				var a = preset[p];
				if(typeof(a) == "object"){
					if(a.constructor == Array){
						dictionary[p] = new Object();
						var i;
						var num = a.length;
						for(i=0;i<num;i++){
							var o = a[i];
							if(o.id !== undefined){
								dictionary[p][o.id] = o;
							}
						}
					}
				}
			}

			// マージ
			for(p in obj){
				var a = obj[p];
				if(typeof(a) == "object"){
					if(a.constructor == Array){
						var i;
						var num = a.length;
						for(i=0;i<num;i++){
							var o = a[i];
							if(o.id !== undefined){
								if(dictionary[p][o.id] !== undefined){
									o.preset = dictionary[p][o.id].preset;
								}
							}
						}
					}
				}
			}

			return obj;
		}

