			_single_count = 1;
			// 開始関数をセット
			_queue_element.onstart = function(){

				// 読み込みを開始する
				var result = loadXMLHttpRequest(function(result,xhr){

					// ロード完了を通知
					_queue_element.complete();

					// ロード成功
					if(result){
						// レスポンスヘッダオブジェクトを返す
						loadSuccess(ResponseHeadersParseObject(xhr.getAllResponseHeaders()));
						return;

					// 失敗
					}else{
						// キューに再登録
						if(!tryAttachElement(false)){
							// エラーで終了
							loadError();
							return;
						}
					}
				});

				// ロードエラー
				if(!result){
					// ロード完了を通知
					_queue_element.complete();

					// エラーで終了
					loadError();
					return;
				}
			};

			// キューに登録
