	// --------------------------------------------------------------------------------
	// レスアンカー番号管理
	// --------------------------------------------------------------------------------
	function ResponseAnchorNumbers(){
		var _container = new Object();

		// --------------------------------------------------------------------------------
		// 番号追加
		// --------------------------------------------------------------------------------
		_container.addNumber = function(number){
			var p;
			var n;
			var list = _list.n;
			while(list != _list){
				if(list.num >= number)	break;
				list = list.n;
			}

			if((list != _list) && (list.num == number)){
			}else{
				var list_min = {num:number,d:0};
				n = list;
				p = n.p;
				list_min.p = p;
				list_min.n = n;
				p.n = list_min;
				n.p = list_min;
			}
		};

		// --------------------------------------------------------------------------------
		// 範囲追加
		// --------------------------------------------------------------------------------
		_container.addNumbers = function(min,max){
			if(min > max){
				var t = max;
				max = min;
				min = t;
			}
			var p;
			var n;
			var list = _list.n;
			while(list != _list){
				if(list.num >= min)	break;
				list = list.n;
			}

			// 最小値
			if((list != _list) && (list.num == min)){
				list.d += 1;
			}else{
				var list_min = {num:min,d:1};
				n = list;
				p = n.p;
				list_min.p = p;
				list_min.n = n;
				p.n = list_min;
				n.p = list_min;
				list = list_min;
			}

			while(list != _list){
				if(list.num >= max)	break;
				list = list.n;
			}

			// 最大値
			if((list != _list) && (list.num == max)){
				list.d -= 1;
			}else{
				var list_max = {num:max,d:-1};
				n = list;
				p = n.p;
				list_max.p = p;
				list_max.n = n;
				p.n = list_max;
				n.p = list_max;
			}
		};

		// --------------------------------------------------------------------------------
		// すべての番号を取得
		// --------------------------------------------------------------------------------
		_container.getNumbers = function(func){
			var d = 0;
			var min = 0;
			var max = 0;
			var list = _list.n;
			while(list != _list){

				d += list.d;

				if(d == 0){
					func(list.num);
				}else if(d > 0){
					min = list.num;

					list = list.n;
					while(list != _list){
						d += list.d;
						if(d == 0){
							max = list.num + 1;
							break;
						}
						list = list.n;
					}
					
					var i;
					for(i=min;i<max;i++){
						func(i);
					}
				}

				list = list.n;
			}
		};

		// --------------------------------------------------------------------------------
		// 総数を取得
		// --------------------------------------------------------------------------------
		_container.getCount = function(){
			var count = 0;
			var d = 0;
			var min = 0;
			var max = 0;
			var list = _list.n;
			while(list != _list){

				d += list.d;

				if(d == 0){
					count += 1;
				}else if(d > 0){
					min = list.num;

					list = list.n;
					while(list != _list){
						d += list.d;
						if(d == 0){
							max = list.num + 1;
							break;
						}
						list = list.n;
					}
					count += max - min;
				}

				list = list.n;
			}
			return count;
		};

		// --------------------------------------------------------------------------------
		// プライベート変数
		// --------------------------------------------------------------------------------
		var _list;

		// --------------------------------------------------------------------------------
		// 初期化
		// --------------------------------------------------------------------------------
		(function(){
			_list = new Object();
			_list.p = _list;
			_list.n = _list;
		})();

		return _container;
	}

	// --------------------------------------------------------------------------------
	// アンカーの更新監視
	// --------------------------------------------------------------------------------
	function AnchorMonitor(){
		var _container = new Object();

		// --------------------------------------------------------------------------------
		// 要素生成
		// --------------------------------------------------------------------------------
		_container.createElement = function(){
			var _element = new Object();

			// --------------------------------------------------------------------------------
			// コールバック関数をセット
			// --------------------------------------------------------------------------------
			_element.setFunction = function(f){
				_element.callback = f;
			};

			// --------------------------------------------------------------------------------
			// URL を設定
			// --------------------------------------------------------------------------------
			_element.setBaseUrl = function(url){
				_element.base_url = url;
			};

			// --------------------------------------------------------------------------------
			// 要素をセット
			// --------------------------------------------------------------------------------
			_element.setAnchorElement = function(anchor){
				_element.anchor_element = anchor;
				_element.base_url = anchor.href;

				if(MutationObserverSupported()){
					_mutation_observer = MutationObserverCreate(function(mutations) {
						var i;
						var num = mutations.length;
						for(i=0;i<num;i++){
							var target = mutations[i].target;
							if(_element.base_url != target.href){
								_element.base_url = target.href;
								_element.callback();
								return;
							}
						}
					});
					_mutation_observer.observe(anchor,{attributes:true,attributeFilter:["href"]});
				}
			};

			// --------------------------------------------------------------------------------
			// 開放
			// --------------------------------------------------------------------------------
			_element.release = function(){
				if(_list == _element){
					_list = _element.next;
				}
				var next = _element.next;
				var prev = _element.prev;
				prev.next = next;
				next.prev = prev;
				_element.prev = _element;
				_element.next = _element;
				_count --;

				if(_mutation_observer){
					_mutation_observer.disconnect();
					_mutation_observer = null;
				}
			};

			// --------------------------------------------------------------------------------
			// プライベート変数
			// --------------------------------------------------------------------------------
			var _mutation_observer;

			// --------------------------------------------------------------------------------
			// 初期化
			// --------------------------------------------------------------------------------
			(function(){
				_element.callback = null;
				_element.anchor_element = null;
				_element.base_url = null;

				var next = _list;
				var prev = next.prev;
				prev.next = _element;
				next.prev = _element;
				_element.prev = prev;
				_element.next = next;
				_count++;
			})();

			return _element;
		}

		// --------------------------------------------------------------------------------
		// 実行
		// --------------------------------------------------------------------------------
		_container.execute = function(){
			var i = 0;
			var num = 5;
			var start = _list;
			do{
				if(_list.anchor_element){
					if(_list.base_url != _list.anchor_element.href){
						_list.base_url = _list.anchor_element.href;
						_list.callback();
					}
				}
				_list = _list.next;
				i ++;
				if(num <= i)	break;
			}while(start != _list);
		}

		// --------------------------------------------------------------------------------
		// 要素数を取得
		// --------------------------------------------------------------------------------
		_container.getCount = function(){
			return _count;
		}

		// --------------------------------------------------------------------------------
		// プライベート変数
		// --------------------------------------------------------------------------------
		var _ring_list;
		var _list;
		var _count;

		// --------------------------------------------------------------------------------
		// 初期化
		// --------------------------------------------------------------------------------
		(function(){
			_ring_list = new Object();
			_ring_list.prev = _ring_list;
			_ring_list.next = _ring_list;
			_list = _ring_list;

			_count = 0;
		})();

		return _container;
	}


