	function ResponseHeadersParseObject(str){
		var o = new Object();
		if(!str)	return o;

		// 改行コードを統一
		var s = str.replace(/\r\n?/g,"\n");
		var a = s.split("\n");
		var i;
		var num = a.length;
		for(i=0;i<num;i++){
			var m = a[i].match(new RegExp("^(.*):[ ](.*)$","i"));
			if(m){
				o[m[1]] = m[2];
			}
		}
		return o;
	}

