	// --------------------------------------------------------------------------------
	// ローカルストレージからロード
	// --------------------------------------------------------------------------------
	function LocalStorageGetItem(key,func){

		function LocalStorageLoad(){
			try{
				if(window.localStorage){
					func({result:true,value:localStorage.getItem(key)});
				}else{
					func({result:false,message:"Do not support storage."});
				}
			}catch(e){
				func({result:false,message:e});
			}
		}

		if(chrome.storage){
			chrome.storage.local.get([key],function(items){
				if(chrome.runtime.lastError){
					func({result:false,message:chrome.runtime.lastError.message});
				}else{
					if(items[key]){
						func({result:true,value:items[key]});
					}else{
						LocalStorageLoad();
					}
				}
			});
		}else{
			LocalStorageLoad();
		}
	}

