	// --------------------------------------------------------------------------------
	// 文字列とアスタリスク付きワードとが一致するか調べる
	// --------------------------------------------------------------------------------
	function StringMatchAsteriskWord(src,w){
		var i_num = src.length;
		var j_num = w.length;
		var i=0;
		var j=0;
		var n;
		var l;
		n = w.indexOf("*");
		if(n < 0){
			return (src == w);
		}else if(n > 0){
			var l = n - j;
			n = src.indexOf(w.substring(j,n),i);
			if(n != 0){
				return false;
			}
			i = n + l;
			j = j + l;
		}
		while(true){
			while(true){
				j ++;
				n = w.indexOf("*",j);
				if(n != j ){
					break;
				}
			}
			if(n < 0){
				if(j >= j_num){
					return true;
				}else{
					n = j_num;
				}
			}
			l = n - j;
			n = src.indexOf(w.substring(j,n),i);
			if(n < 0){
				return false;
			}
			i = n + l;
			j = j + l ;
		}
		return false;
	}

	// --------------------------------------------------------------------------------
	// URL 文字列とアスタリスク付きワードとが一致するか調べる
	// --------------------------------------------------------------------------------
	function StringUrlMatchAsteriskWord(src,w){

		var ary = new Array();
		var r = new RegExp(":|\/|[.]|[?]|#|[*]+","i");
		while(true){
			var m = w.match(r);
			if(m){
				if(RegExp.leftContext){
					ary.push(RegExp.leftContext);
				}
				if(m[0].charAt(0) == '*')	ary.push('*');
				else						ary.push(m[0]);
				w = RegExp.rightContext;
				continue;
			}
			if(w){
				ary.push(w);
			}
			break;
		}

		var i;
		var num = src.length;
		var p = 0;
		while(true){
			w = ary.shift();
			if(!w){
				if(p >= num)	return true;
				return false;
			}
			if(w == '*'){
				w = ary.shift();
				if(!w)	return true;

				i = src.indexOf(w,p);
				if(i < 0)	return false;
				p = i + w.length;
			}else{
				if(src.indexOf(w,p) != p)	return false;
				p += w.length;
			}
		}
	}

	// --------------------------------------------------------------------------------
	// URLアスタリスク付きワードから正規表現配列を取得
	// --------------------------------------------------------------------------------
	function StringRegExpListFromAsteriskWord(w){

		var str = "^";
		var ary = new Array();
		var r = new RegExp(":|\/|[.]|[?]|#|[*]+");

		while(true){
			var m = w.match(r);
			if(m){
				if(m[0].charAt(0) == '*'){
					str += RegExp.leftContext + ".*?";
				}else{
					str += RegExp.leftContext + "[" + m[0] + "]";
					ary.push(new RegExp(str));
					str = "^";
				}
				w = RegExp.rightContext;
			}else{
				m = w.match(new RegExp("(.*)$"));
				if(m[0] || (str.length > 1)){
					str += m[0] + "$";
					ary.push(new RegExp(str));
					break;
				}
				break;
			}
		}
		return ary
	}

	// --------------------------------------------------------------------------------
	// URL 文字列とアスタリスク付きワードとが一致するか調べる
	// --------------------------------------------------------------------------------
	function StringUrlMatchRegExpList(src,ary){
		var i;
		var num = ary.length;
		var m;
		for(i=0;i<num;i++){
			m = src.match(ary[i]);
			if(!m)	return false;
			src = RegExp.rightContext;
		}

		if(src == "")	return true;
		return false;
	}

	// --------------------------------------------------------------------------------
	// HTML 文字列から DOM オブジェクトを生成（スクリプト要素 と イベントハンドラは除外）
	// --------------------------------------------------------------------------------
	function StringHtmlCreateDomNodesSafe(html){
		var nodes = new Array();
		var stack = new Array();

		var c;
		var b;
		var e;
		var p = 0;
		var l = html.length;
		
		var edit = {
			element:null,
			name:"",
			depth:0
		};
		stack.push(edit);

		try{
			while(true){
				// SCRIPT、STYLE
				if(edit.name == "SCRIPT" || edit.name == "STYLE"){

					while(true){

						// '<'
						while(l > p){
							c = html.charAt(p);
							p ++;
							if(c == '<'){
								break;
							}
						}
						if(p >= l)	throw("end");

						// 整形無視
						while(l > p){
							c = html.charAt(p);
							p ++;
							if(c == ' '){
							}else if(c == '　'){
							}else if(c == '\t'){
							}else if(c == '\r'){
							}else if(c == '\n'){
							}else{
								break;
							}
						}
						if(p >= l)	throw("end");

						// '/'
						if(c != '/')	continue;

						// 整形無視
						while(l > p){
							c = html.charAt(p);
							if(c == ' '){
							}else if(c == '　'){
							}else if(c == '\t'){
							}else if(c == '\r'){
							}else if(c == '\n'){
							}else{
								break;
							}
							p ++;
						}
						if(p >= l)	throw("end");

						// 要素名
						var tag_name = edit.name;
						if(html.substr(p,tag_name.length).toUpperCase() != tag_name)	continue;
						p += tag_name.length;

						// 整形無視
						while(l > p){
							c = html.charAt(p);
							p ++;
							if(c == ' '){
							}else if(c == '　'){
							}else if(c == '\t'){
							}else if(c == '\r'){
							}else if(c == '\n'){
							}else{
								break;
							}
						}
						if(p >= l)	throw("end");

						// '>'
						if(c != '>')	continue;
						if(html.charAt(p - 2) == '\\')	continue;

						// スタックポップ
						if(edit.depth){
							stack.pop();
							edit = stack[stack.length - 1];
						}
						break;
					}

				}

				// 要素の開始 '<'
				b = p;
				e = -1;
				while(l > p){
					c = html.charAt(p);
					p ++;
					if(c == '<'){
						e = p - 1;
						break;
					}
				}

				// テキストノード
				if(b < e){
					// テキストノード生成
					var s = html.substring(b,e);
					s = s.replace(/&gt;/g, ">");
					s = s.replace(/&lt;/g, "<");
					s = s.replace(/&quot;/g, "\"");
					s = s.replace(/&nbsp;/g, function(str) { return String.fromCharCode(0x00a0); });
					s = s.replace(/&#([0-9]+);/g, function(str) { return String.fromCharCode(RegExp.$1); });
					s = s.replace(/&amp;/g, "&");
					var text_node = document.createTextNode(s);
					if(text_node){
						if(edit.depth){
							if(edit.element){
								edit.element.appendChild(text_node);
							}
						}else{
							nodes.push(text_node);
						}
					}
				}
				if(p >= l)	throw("end");

				// コメント "<!--"
				if(html.substr(p,3) == "!--"){
					p += 3;
					e = html.indexOf("-->",p);
					if(e >= 0)	p = e + 3;
					continue;
				}

				// 名前の開始、要素の終了 '/'
				while(l > p){
					c = html.charCodeAt(p);
					p ++;
					if((0x41 <= c && c <= 0x5a) || (0x61 <= c && c <= 0x7a) || (c == 0x2f)){
						b = p - 1;
						break;
					}
				}
				if(p >= l)	throw("end");

				// 要素の終了 '/'
				if(c == 0x2f){

					// 名前の開始
					while(l > p){
						c = html.charCodeAt(p);
						p ++;
						if((0x41 <= c && c <= 0x5a) || (0x61 <= c && c <= 0x7a)){
							b = p - 1;
							break;
						}
					}
					if(p >= l)	throw("end");

					// 名前の終了
					while(l > p){
						c = html.charCodeAt(p);
						if((0x41 <= c && c <= 0x5a) || (0x61 <= c && c <= 0x7a) || (0x30 <= c && c <= 0x39)){
						}else{
							e = p;
							break;
						}
						p ++;
					}
					if(p >= l)	throw("end");

					// 要素名
					var tag_name = html.substring(b,e).toUpperCase();

					// 要素の終了 '>'
					while(l > p){
						c = html.charAt(p);
						p ++;
						if(c == '>'){
							break;
						}
					}

					// スタックポップ
					var i;
					b = e = stack.length;
					for(i=e-1;i>=0;i--){
						if(stack[i].name == tag_name){
							b = i;
							break;
						}
					}
					for(;b<e;b++){
						if(edit.depth){
							stack.pop();
							edit = stack[stack.length - 1];
						}
					}

				// 名前の開始
				}else{
					// 名前の終了
					while(l > p){
						c = html.charCodeAt(p);
						if((0x41 <= c && c <= 0x5a) || (0x61 <= c && c <= 0x7a) || (0x30 <= c && c <= 0x3a)){
						}else{
							e = p;
							break;
						}
						p ++;
					}
					if(p >= l)	throw("end");

					// 要素名
					var tag_name = html.substring(b,e).toUpperCase();

					// 要素終了
					var tag_list = ["LI","DT","DD"];
					var i;
					var j;
					var num = tag_list.length;
					for(i=0;i<num;i++){
						if(tag_name == tag_list[i]){
							break;
						}
					}
					if(i < num){
						b = e = stack.length;
						for(i=e-1;i>=0;i--){
							for(j=0;j<num;j++){
								if(stack[i].name == tag_list[j]){
									b = i;
									break;
								}
							}
						}
						for(;b<e;b++){
							if(edit.depth){
								stack.pop();
								edit = stack[stack.length - 1];
							}
						}
					}

					// 要素生成
					var parent = edit.element;
					var empty = false;
					var element = null;
					switch(tag_name){
					case "HTML":
					case "HEAD":
					case "BODY":
					case "SCRIPT":
					case "STYLE":
					case "IFRAME":
						break;
					default:
						element = document.createElement(tag_name);
						break;
					};
					if(element){
						if(edit.depth){
							if(parent && element){
								parent.appendChild(element);
							}
						}else{
							nodes.push(element);
						}
					}

					// 空要素
					switch(tag_name){
					case "META":
					case "LINK":
					case "IMG":
					case "HR":
					case "BR":
					case "INPUT":
						empty = true;
						break;
					};

					// スタックプッシュ
					edit = {
						element:element,
						name:tag_name,
						depth:edit.depth + 1
					};
					stack.push(edit);

					// 属性
					while(true){
						// 整形無視
						while(l > p){
							c = html.charAt(p);

							// 整形無視
							if(c == ' '){
							}else if(c == '　'){
							}else if(c == '\t'){
							}else if(c == '\r'){
							}else if(c == '\n'){
							}else{
								b = p;
								break;
							}
							p ++;
						}
						if(p >= l)	throw("end");

						// 要素の終了 '>'
						if(c == '>'){
							// スタックポップ
							if(empty){
								if(edit.depth){
									stack.pop();
									edit = stack[stack.length - 1];
								}
							};
							p += 1;
							break;

						// 空要素 '/'
						}else if(c == '/'){
							empty = true;
							p += 1;

						// 属性名の開始
						}else{
							// 属性名の終了
							while(l > p){
								c = html.charAt(p);
								if((c == ' ') || (c == '　') || (c == ' ') || (c == '\t') || (c == '\r') || (c == '\n') || (c == '>') || (c == '=')){
									e = p;
									break;
								}
								p ++;

							}
							if(p >= l)	throw("end");

							var attr_name = html.substring(b,e);
							var attr_value = undefined;

							// 整形無視
							while(l > p){
								c = html.charAt(p);
								if(c == ' '){
								}else if(c == '　'){
								}else if(c == '\t'){
								}else if(c == '\r'){
								}else if(c == '\n'){
								}else{
									break;
								}
								p ++;
							}
							if(p >= l)	throw("end");

							// 属性値 '='
							if(c == '='){
								p ++;

								// 整形無視
								while(l > p){
									c = html.charAt(p);
									if(c == ' '){
									}else if(c == '　'){
									}else if(c == '\t'){
									}else if(c == '\r'){
									}else if(c == '\n'){
									}else{
										break;
									}
									p ++;
								}
								if(p >= l)	throw("end");

								// 引用符
								var li = "";
								if(c == '"' || c == '\''){
									li = c;
									p ++;
								}

								b = p;

								// 属性値の終了、引用符の終了
								while(l > p){
									c = html.charAt(p);

									if(li){
										// 引用符の終了
										if(c == li){
											if(html.charAt(p - 1) != '\\'){
												e = p;
												p ++;
												break;
											}
										}
									}else{
										// 属性値の終了
										if((c == ' ') || (c == '　') || (c == ' ') || (c == '\t') || (c == '\r') || (c == '\n') || (c == '>')){
											e = p;
											break;
										}
									}

									p ++;
								}
								if(p >= l)	throw("end");

								// 属性値
								attr_value = html.substring(b,e);

							}

							if(edit.element){
								if(attr_name.toLowerCase().indexOf("on") == 0){
								}else{
									if(attr_value === undefined)	attr_value = "";

									// 属性生成
									var attribute = document.createAttribute(attr_name);
									attribute.value = attr_value;
									edit.element.setAttributeNode(attribute);
								}
							}
						}
					}
				}
			}
		}catch(e){}

		return nodes;
	}

	// --------------------------------------------------------------------------------
	// オブジェクトをコピー
	// --------------------------------------------------------------------------------
	function ObjectCopy(obj){
		if(!obj)	return null;

		var o;
		var s;
		var p;
		var root = new (obj.constructor)();
		var que = new Array();

		que.push({o:root,s:obj});

		while(true){
			p = que.pop();
			if(!p)	break;

			o = p.o;
			s = p.s;
			if(s.constructor == Array){
				var num = s.length;
				for(p=0;p<num;p++){
					switch(typeof(s[p])){
					case "boolean":
					case "number":
					case "string":
					case "null":
						o[p] = s[p];
						break;
					case "object":
						o[p] = new (s[p].constructor)();
						que.push({o:o[p],s:s[p]});
						break;
					};
				}
			}else{
				for(p in s){
					switch(typeof(s[p])){
					case "boolean":
					case "number":
					case "string":
					case "null":
						o[p] = s[p];
						break;
					case "object":
						o[p] = new (s[p].constructor)();
						que.push({o:o[p],s:s[p]});
						break;
					};
				}
			}
		}

		return root;
	}

