	// --------------------------------------------------------------------------------
	// DOMノードが外れたか監視
	// --------------------------------------------------------------------------------
	function DocumentObserverRemoveDomNode(){
		var _container = new Object();

		// --------------------------------------------------------------------------------
		// 開放
		// --------------------------------------------------------------------------------
		_container.release = function(){
			if(_mutation_observer){
				_mutation_observer.disconnect();
				_mutation_observer = null;
			}
		}

		// --------------------------------------------------------------------------------
		// 要素生成
		// --------------------------------------------------------------------------------
		_container.createElement = function(){
			var _element = new Object();

			// --------------------------------------------------------------------------------
			// コールバック関数をセット
			// --------------------------------------------------------------------------------
			_element.setFunction = function(f){
				_element.callback = f;
			};

			// --------------------------------------------------------------------------------
			// 要素をセット
			// --------------------------------------------------------------------------------
			_element.setDomNode = function(node){
				_element.node = node;

				if(_mutation_observer){
					_id = node._pageexpand_remove;
					if(!_id){
						_id = node._pageexpand_remove = _identity;
						_identity++;
					}

					if(!_list[_id]){
						var o = new Object();
						o._prev = o;
						o._next = o;
						_list[_id] = o;
					}

					var _prev = _list[_id];
					var _next = _prev._next;
					_prev._next = _element;
					_next._prev = _element;
					_element._prev = _prev;
					_element._next = _next;
				}
			};

			// --------------------------------------------------------------------------------
			// 開放
			// --------------------------------------------------------------------------------
			_element.release = function(){
				if(_mutation_observer){
					var _prev = _element._prev;
					var _next = _element._next;
					_prev._next = _next;
					_next._prev = _prev;
					_element._prev = _element;
					_element._next = _element;

					if(_list[_id] == _list[_id].next){
						ObjectDeleteProperty(_list,_id);
					}

				}else{
					if(_list_end == _element){
						_list_end = _element._prev;
					}
					if(_list_pos == _element){
						_list_pos = _element._prev;
					}

					var _prev = _element._prev;
					var _next = _element._next;
					_prev._next = _next;
					_next._prev = _prev;
					_element._prev = _element;
					_element._next = _element;
				}

				_count -= 1;
			};

			// --------------------------------------------------------------------------------
			// プライベート変数
			// --------------------------------------------------------------------------------
			var _id;

			// --------------------------------------------------------------------------------
			// 初期化
			// --------------------------------------------------------------------------------
			(function(){
				if(_mutation_observer){
					_element._prev = _element;
					_element._next = _element;
				}else{
					var _next = _list_pos;
					var _prev = _next._prev;
					_element._prev = _prev;
					_element._next = _next;
					_next._prev = _element;
					_prev._next = _element;

					_list_end = _element;
				}

				_element.callback = null;
				_element.node = null;

				_count += 1;
			})();

			return _element;
		}

		// --------------------------------------------------------------------------------
		// 要素数を取得
		// --------------------------------------------------------------------------------
		_container.getCount = function(){
			return _count;
		}

		// --------------------------------------------------------------------------------
		// 実行
		// --------------------------------------------------------------------------------
		_container.execute = function(){
			_list_end = _list_pos;
			var num = 5;
			var i;
			for(i=0;i<num;i++){
				if(_list_pos.node){
					if(!(DomNodeGetAttachedDocument(_list_pos.node))){
						_list_pos.callback();
					}
				}
				_list_pos = _list_pos._next;
				if(_list_pos == _list_end){
					return;
				}
			}
		}

		// --------------------------------------------------------------------------------
		// 検出
		// --------------------------------------------------------------------------------
		function detect(node){
			var w;
			var o;
			var p;
			var n;
			var i = 0;
			var c = 0;
			var m = 100;
			var q = new Object();
			var nodes = node.childNodes;
			q.p = q;
			q.n = q;

			// 中断
			function interrupt(){
				c = 0;
				execute_queue.attachFirstForRemoveDomNode(analyze,null);
			}

			// 子孫抽出
			function analyze(){
				while(node){
					if(nodes){
						while(nodes[i]){
							o = {node:nodes[i]};
							n = q;
							p = n.p;
							o.p = p;
							o.n = n;
							p.n = o;
							n.p = o;
							i++;
							c++;
							if(c > m){
								interrupt();
								return;
							}
						}
					}

					var id = node._pageexpand_remove;
					if(id){
						if(!(DomNodeGetAttachedDocument(node))){
							var ary = new Array();
							var dict = _list[id];
							var list = dict._next;
							while(list != dict){
								ary.push(list);
								list = list._next;
							}

							var j;
							var num = ary.length;
							for(j=0;j<num;j++){
								ary[j].callback();
							}
						}
					}

					o = q.n;
					p = o.p;
					n = o.n;
					p.n = n;
					n.p = p;
					node = o.node;
					if(node){
						i = 0;
						nodes = node.childNodes;
					}

					c++;
					if(c > m){
						interrupt();
						return;
					}
				}
			}

			analyze();
		}

		// --------------------------------------------------------------------------------
		// プライベート変数
		// --------------------------------------------------------------------------------
		var _mutation_observer;
		var _task;
		var _list;
		var _list_pos;
		var _list_end;
		var _count;
		var _identity;

		// --------------------------------------------------------------------------------
		// 初期化
		// --------------------------------------------------------------------------------
		(function(){
			_count = 0;

			if(MutationObserverSupported()){
				_list = new Array();
				_identity = 1;
				_mutation_observer = MutationObserverCreate(function(mutations) {
					var i;
					var num = mutations.length;
					for(i=0;i<num;i++){
						var nodes = mutations[i].removedNodes;
						var j;
						var node_num = nodes.length;
						for(j=0;j<node_num;j++){
							execute_queue.attachLastForRemoveDomNode(detect,nodes[j]);
						}
					}
				});
				_mutation_observer.observe(document.documentElement,{childList:true,subtree:true});
			}

			if(!_mutation_observer){
				_list = new Object();
				_list._prev = _list;
				_list._next = _list;
				_list_pos = _list;
				_list_end = _list;
			}
		})();

		return _container;
	}

	// --------------------------------------------------------------------------------
	// スクロール監視
	// --------------------------------------------------------------------------------
	function DocumentObserverScroll(){
		var _container = new Object();

		// --------------------------------------------------------------------------------
		// 開放
		// --------------------------------------------------------------------------------
		_container.release = function(){
			if(window.addEventListener){
				window.addEventListener("scroll",scroll_func,true);
			}
		}

		// --------------------------------------------------------------------------------
		// 要素生成
		// --------------------------------------------------------------------------------
		_container.createElement = function(){
			var _element = new Object();

			// --------------------------------------------------------------------------------
			// コールバック関数をセット
			// --------------------------------------------------------------------------------
			_element.setFunction = function(f){
				_element.callback = f;
			};

			// --------------------------------------------------------------------------------
			// 開放
			// --------------------------------------------------------------------------------
			_element.release = function(){
				if(_list_end == _element){
					_list_end = _element._prev;
				}
				if(_list_pos == _element){
					_list_pos = _element._prev;
				}

				var _prev = _element._prev;
				var _next = _element._next;
				_prev._next = _next;
				_next._prev = _prev;
				_element._prev = _element;
				_element._next = _element;

				_count -= 1;
			};

			// --------------------------------------------------------------------------------
			// 初期化
			// --------------------------------------------------------------------------------
			(function(){
				var _next = _list_pos;
				var _prev = _next._prev;
				_element._prev = _prev;
				_element._next = _next;
				_next._prev = _element;
				_prev._next = _element;

				_list_end = _element;

				_element.callback = null;

				_count += 1;
			})();

			return _element;
		}

		// --------------------------------------------------------------------------------
		// 要素数を取得
		// --------------------------------------------------------------------------------
		_container.getCount = function(){
			return _count;
		}

		// --------------------------------------------------------------------------------
		// 検出
		// --------------------------------------------------------------------------------
		function detect(){
			if(_task){
				_list_end = _list_pos._prev;
			}else{
				_task = task_container.createTask();
				_task.setExecuteFunc(function(){
					var num = 10;
					var i;
					for(i=0;i<num;i++){
						if(_list_pos.callback){
							_list_pos.callback();
						}

						_list_pos = _list_pos._next;

						if(_list_pos == _list_end){
							_task.release();
							_task = null;
							return;
						}
					}
				});

				_list_pos = _list;
				_list_end = _list_pos._prev;

				_task.execute(0xffffffff);
			}
		}

		// --------------------------------------------------------------------------------
		// プライベート変数
		// --------------------------------------------------------------------------------
		var _mutation_observer;
		var _task;
		var _list;
		var _list_pos;
		var _list_end;
		var _count;

		// --------------------------------------------------------------------------------
		// 初期化
		// --------------------------------------------------------------------------------
		(function(){
			_count = 0;
			_list = new Object();
			_list._prev = _list;
			_list._next = _list;
			_list_pos = _list;
			_list_end = _list;

			if(window.addEventListener){
				window.addEventListener("scroll",detect,true);
			}
		})();

		return _container;
	}

	// --------------------------------------------------------------------------------
	// エレメントからタグ名を指定してエレメントを取得
	// --------------------------------------------------------------------------------
	function ElementGetElementsByTagName(node,type){
		if(node.getElementsByTagName){
			return node.getElementsByTagName(type);
		}else if(node.querySelectorAll){
			return node.querySelectorAll(type);
		}else{
			type = type.toUpperCase();
			var ary = new Array();
			var p;
			var n;
			var q;
			var queue = new Object();
			q = {p:queue,n:queue,node:node};
			queue.p = q;
			queue.n = q;

			while(queue.n != queue){
				q = queue.n;
				p = q.p;
				n = q.n;
				p.n = n;
				n.p = p;
				var node = q.node;
				switch(node.nodeType){
				case 1:
					var i;
					var nodes = node.childNodes;
					var num = nodes.length;
					for(i=0;i<num;i++){
						n = queue;
						p = n.p;
						q = {p:p,n:n,node:nodes[i]};
						p.n = q;
						n.p = q;
					}
					if(node.tagName == type || type == '*'){
						ary.push(node);
					}
					break;
				};
			}
			return ary;
		}
	}

